import { View, Text  , ActivityIndicator , FlatList , StyleSheet , ScrollView} from 'react-native'
import React, { useEffect, useState } from 'react'
import axios from "axios"

const NewsList = () => {

  const[articles , setArticles] =   useState([])

  useEffect(()=>{
   const base_url = 'https://newsapi.org/v2/top-headlines?category=business&language=en&apiKey=3f3b61c5f48445e594d57355f0303984'

    axios.get(base_url )
    .then(res=>{
      // console.log("res : " ,  res.data.articles);
      setArticles(res.data.articles)
    })
    .catch(e=>{
      console.log("error : " , e)
    })
  } , [])

  return (
    
    <View style={styles.container}>

    <Text style = {{justifyContent :'center' , fontSize : 20 , fontWeight : '600'}}> Top News For You </Text>

    { articles.length != 0 ?
      <FlatList
        data={articles}
        keyExtractor={(item) => item.title}
        renderItem={({ item }) => (
          <View style={styles.newsItem}>
            <Text style={styles.title}>{item.title}</Text>
            <Text style={styles.description}>{item.description}</Text>
            <Text style={styles.source}>Source: {item.source.name}</Text>
          </View>
        )}
      />
      :
      <ActivityIndicator size="large" color="#007BFF" />
    }
    </View>
  )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
    alignItems: 'center',
  },
  newsItem: {
    marginBottom: 16,
    backgroundColor: '#f5f5f5',
    padding: 12,
    borderRadius: 8,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 16,
    marginTop: 8,
  },
  source: {
    fontSize: 14,
    color: 'gray',
  },
});





export default NewsList


