import React,{useState} from 'react'
import { View, Text  , StyleSheet , TouchableOpacity   } from 'react-native'
import {FontAwesome, MaterialIcons} from  '@expo/vector-icons'
// import { useNavigation } from '@react-navigation/native';
import { useSelector} from 'react-redux'


import { MaterialCommunityIcons } from '@expo/vector-icons';
import colors from '../../Constants/colors';

export default function Profile(){

  const user = {name:'vikash' , email:'vk@gmail.com'}

//   const nav = useNavigation();
// const dispatch = useDispatch();

const tabData  = [
{
  id : 1 ,
  title : 'Edit Profile' ,
  describe : 'Add or modify name , mobile number ,email',
  icon : "account-edit",
  onClick : ''
},
{
  id : 2 ,
  title : 'Saved Address' ,
  describe : 'Modify address of your farm or home where you want your shoped products to be delivered',
  icon : "edit-location",
  onClick : ''
},
{
  id : 3 ,
  title : 'Saved Address' ,
  describe : 'Modify address of your farm or home where you want your shoped products to be delivered',
  icon : "edit-location",
  onClick : ''
},
{
  id : 4 ,
  title : 'Saved Address' ,
  describe : 'Modify address of your farm or home where you want your shoped products to be delivered',
  icon : "edit-location",
  onClick : ''
},

]


  return (
    user ? 
    <View style={styles.container}>

    
    <View style = {styles.userView}>
    <FontAwesome name =  'user-circle-o'   size = {50} />
    <Text style={styles.name}>{user.name}</Text>
      <Text style={styles.email}>{user.email} </Text>
    </View>


    <View style = {styles.tabView}>



    <FlatList  
    data={tabData}
    keyExtractor={(item) => item.id}
    renderItem={ ({data}) =>

    <TouchableOpacity style={styles.tab} id={data.title}>

    <View style = {{flexDirection:'row'}}>
      <MaterialCommunityIcons name={data.icon} size={34} color="black" />
      <View style ={styles.titleView} >
      <Text style={styles.title}>{data.title}</Text>
      <Text style={styles.describe}>{data.describe}</Text>
      </View>
    </View>

    <MaterialIcons name="navigate-next" size={34} color="black" />
    
  </TouchableOpacity>


     }
  />

      
    </View>
    


    </View>
    :
    <></>
    // <AuthPrompt onClose = {'HomeScreen'} />
  )
}

const styles = StyleSheet.create({
  container : {
    flex:1
  },
  userView : {
marginTop : 20 , 
justifyContent : 'center' ,
alignItems : 'center',
marginBottom : 30 ,
  },
  name: {
    marginTop: 10,
    fontSize: 20,
    fontWeight: '400',
    textTransform : 'capitalize',
    color: colors.text,
  },
email :{
  fontSize: 20,
    fontWeight: '400',
    color:colors.text,
},

tabView:{
    marginTop: 5 , 
    marginBottom : 10 , 
    backgroundColor : colors.light_green,
    paddingVertical : 10,
    borderRadius : 5 ,
    justifyContent : 'center' ,
    alignItems : 'center',
    marginHorizontal : 10,
    paddingHorizontal : 10 

},

  tab: {
    marginTop: 10,
    height: 60,
    borderBottomWidth: 0.3,
    borderBottomColor: 'black',
    justifyContent: 'center',
    flexDirection:'row',
    justifyContent : 'space-between',
    alignItems : 'center'
  },

  titleView:{
    marginLeft : 5 ,
    flexDirection :'column' ,
      width : "80%"
    },

  title :{
    fontSize : 15 ,
    fontWeight : '600' ,
  },
  describe : {
    fontSize : 10 ,
    fontWeight : '400' ,
    
  }



})
